<style>
	section {
		position: absolute;
		top: 50%;
		left: 50%;
		transform: translate(-50%, -50%);
		color: var(--white);
		display: inline-block;
		white-space: pre;
		user-select: none;
		letter-spacing: 0;
		line-height: 1.4;
		font-family: 'nb-television-2d';
		font-size: 12.05px;
		background: var(--black);
	}

	@media only screen and (max-width: 768px) {
		section {
			left: -50%;
			top: 0;
			transform: scaleY(1);
			color: var(--white-50);
		}
	}
</style>
